import React from "react";
import { Card, CardContent, Typography, Grid } from "@material-ui/core";

import CountUp from "react-countup";

import "./cardcss.css";

const Cardi = ({ data: { confirmed, recovered, deaths, lastUpdate } }) => {
  if (!confirmed) {
    return "loading...";
  }
  return (
    <div className="container">
      <Grid container spacing={3} className="main_grid" justify="center">
        <Grid item component={Card} xs={7} md={2} className="infected">
          <CardContent>
            <Typography color="textSecondary" gutterBottom>
              INFECTED
            </Typography>
            <Typography variant="h5">
              <CountUp
                start={0}
                end={confirmed.value}
                duration={3}
                separator=","
              />
            </Typography>
            <Typography color="textSecondary">
              {new Date(lastUpdate).toDateString()}
            </Typography>
            <Typography variant="body2">Number of active cases</Typography>
          </CardContent>
        </Grid>
        <Grid item component={Card} xs={7} md={2} className="card recovered">
          <CardContent>
            <Typography color="textSecondary" gutterBottom>
              RECOVERED
            </Typography>
            <Typography variant="h5">
              <CountUp
                start={0}
                end={recovered.value}
                duration={3}
                separator=","
              />
            </Typography>
            <Typography color="textSecondary">
              {new Date(lastUpdate).toDateString()}
            </Typography>
            <Typography variant="body2">Number of recovered cases</Typography>
          </CardContent>
        </Grid>
        <Grid item component={Card} xs={7} md={2} className="card deaths">
          <CardContent>
            <Typography color="textSecondary" gutterBottom>
              DEATH
            </Typography>
            <Typography variant="h5">
              <CountUp
                start={0}
                end={deaths.value}
                duration={3}
                separator=","
              />
            </Typography>
            <Typography color="textSecondary">
              {new Date(lastUpdate).toDateString()}
            </Typography>
            <Typography variant="body2">Number of death cases</Typography>
          </CardContent>
        </Grid>
      </Grid>
    </div>
  );
};

export default Cardi;
