import React, { useState } from "react";
import AddIcon from "@material-ui/icons/Add";
import RemoveIcon from "@material-ui/icons/Remove";
import Button from "@material-ui/core/Button";
import "./index.css";

let integer = 0;
const App = () => {
  const [num, setNum] = useState(integer);

  const inc = () => {
    integer++;
    setNum(integer);
  };

  const dec = () => {
    if (integer > 0) {
      integer--;
      setNum(integer);
    }
  };
  return (
    <>
      <div className="parent_div">
        <div className="child_div">
          <h1>{num}</h1>
        </div>
        <Button
          className="btn"
          variant="contained"
          color="primary"
          onClick={inc}
        >
          <AddIcon />
        </Button>
        <Button onClick={dec} variant="contained" color="primary">
          <RemoveIcon />
        </Button>
      </div>
    </>
  );
};

export default App;
