import React, { useState } from "react";

const App = () => {
  let integer = 0;
  const [num, setNum] = useState(integer);

  const inc = () => {
    let c = { num };
    c++;
    setNum(c);
  };

  const dec = () => {
    let c = { num };
    if (c > 0) {
      c--;
      setNum(c);
    }
  };
  return (
    <>
      <div className="parent_div">
        <div className="child_div">
          <h1>{num}</h1>
        </div>
        <button onClick={inc}>INCREMENT</button>
        <button onClick={dec}>DECREMENT</button>
      </div>
    </>
  );
};

export default App;
